angular.module("clocks").factory("clockSrvc",function(r,e){return{loadProjects:function(){var o=e.defer();return r({url:"server/index.php/projects/load",method:"GET"}).success(function(r){o.resolve(r)}).error(function(r){console.log(r),o.reject()}),o.promise},insertProject:function(o,n){var t=e.defer();return r({url:"server/index.php/projects/insert",method:"POST",data:{title:o,user:n}}).success(function(r){t.resolve(r)}).error(function(r){console.log(r),t.reject()}),t.promise},convertToSeconds:function(r,e,o){return 3600*r+60*e+o},revertFromSeconds:function(r){var e=Math.floor(r/3600),o=Math.floor((r-3600*e)/60),n=r-3600*e-60*o;return{hours:e,minutes:o,seconds:n}}}});